#import cProfile
class Solution(object):
    def lengthOfLongestSubstring(self, s):
        """
        :type s: str
        :rtype: int
        """

        if len(s) < 2:
            longest_substr = len(s)
        else:
            longest_substr = 0

            s += s[-1]
            substr = ''
            substr_start = 0
            for index, letter in enumerate(s):

                if letter in substr:
                    substr_length = index - substr_start
                    if substr_length > longest_substr:
                        longest_substr = substr_length
                    while letter in substr:
                        substr = substr[1:]
                        substr_start += 1

                substr += letter

        return longest_substr


if __name__ == "__main__":
    s = Solution()
    print(s.lengthOfLongestSubstring('abcabcbb'))  # 3
    print(s.lengthOfLongestSubstring('bbbbb'))  # 1
    print(s.lengthOfLongestSubstring('pwwkew'))  # 3
    print(s.lengthOfLongestSubstring('c'))  # 1
    print(s.lengthOfLongestSubstring('au'))  # 2
    print(s.lengthOfLongestSubstring("dvdf"))  # 3
    #cProfile.run('print(s.lengthOfLongestSubstring(""))')
